import Foundation

func longestCommonSubsequence(_ text1: String, _ text2: String) -> Int {
    if text1.count == 0 || text2.count == 0 {
        return 0
    }
    
    var dp:[[Int]] = Array(repeating: Array(repeating: 0, count: text2.count + 1), count: text1.count + 1)
    
    for i in 1...text1.count {
        let index1 = text1.index(text1.startIndex, offsetBy: i - 1)
        let char1 = text1[index1]
        for j in 1...text2.count {
            let string2 = text2 as NSString //提交超时，转用NSString
//            let index2 = text2.index(text2.startIndex, offsetBy: j - 1)
            if String(char1) == string2.substring(with: NSMakeRange(j-1, 1)) {
                dp[i][j] = dp[i - 1][j - 1] + 1
            } else {
                dp[i][j] = max(dp[i-1][j], dp[i][j-1])
            }
        }
    }
    
    return dp[text1.count][text2.count]
}

private func testLongestCommonSubsequence() {
    let i = ""
    let j = ""
    let res = longestCommonSubsequence(i, j)
    print(res)
}
